import React from 'react';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import Drawer from '@material-ui/core/Drawer';
import Box from '@material-ui/core/Box';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Link from '@material-ui/core/Link';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import NotificationsIcon from '@material-ui/icons/Notifications';
import Button from '@material-ui/core/Button';
import InfoIcon from '@material-ui/icons/Info';
import SearchBar from 'material-ui-search-bar';
import { secondaryListItems } from './listItems';
import Chart from './Chart';
import Deposits from './Deposits';
import Orders from './Orders';
import AdminServices from './adminServices';
import UserServices from './userServices';
import EngAndDev from './engAndDev';
import Miscellenous from './miscellaneous';
import { blue } from '@material-ui/core/colors';

function Copyright() {
  return (
    <Typography variant="body2" color="textSecondary" align="center">
      {'Copyright © '}
      <Link color="inherit" href="https://material-ui.com/">
        Your Website
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const drawerWidth = 250;

const useStyles = makeStyles(theme => ({
  root: {
    display: 'absolute',
  },
  toolbar: {
    color: 'lightblue',
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    top: 75,
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShiftLeft: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  appBarShiftRight: {
    marginRight: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  appBarShiftBoth: {
    marginLeft: drawerWidth,
    marginRight: drawerWidth,
    width: `calc(100% - ${drawerWidth*2}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    top: 75,
    position: 'absolute',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
    elevation: 10,
  },
  drawerPaperClose: {

    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flex: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingLeft: 24,
    paddingTop: theme.spacing(0),
    paddingBottom: theme.spacing(0),
    width: '100hv',
  },
  paper: {
    paddingLeft: 75,
    paddingRight: 75,
    padding: theme.spacing(7),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
    borderTop: 'darkblue',
    borderTopStyle: 'solid',
  },
  paper1: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
    borderTop: 'blue',
    borderTopStyle: 'solid',
  },
  fixedHeight: {
    height: 750,
  },
  // height for the miscellaneous component
  fixedHeight1: {
    height: 375,
  },
  containerHeight: {
    height: 50,
  }
}));

export default function Dashboard() {
  const classes = useStyles();
  const [openLeft, setOpenLeft] = React.useState(true);
  const [openRight, setOpenRight] = React.useState(true);
  const handleLeftDrawerOpen = () => {
    setOpenLeft(false);
  };
  const handleLeftDrawerClose = () => {
    setOpenLeft(true);
  };
  const handleRightDrawerOpen = () => {
    setOpenRight(false);
  };
  const handleRightDrawerClose = () => {
    setOpenRight(true);
  };
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);
  const fixedHeightPaper1 = clsx(classes.paper, classes.fixedHeight1);
  const containerHeightPaper = clsx(classes.paper, classes.containerHeight);
  return (
    <div className={classes.root}>
    <CssBaseline />
      <div/>
      {/* <AppBar position="absolute" className={clsx(classes.appBar, !openLeft && classes.appBarShiftLeft, !openRight && classes.appBarShiftRight, !openRight && !openLeft && classes.appBarShiftBoth)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleLeftDrawerOpen}
            className={clsx(classes.menuButton, !openLeft && classes.menuButtonHidden)}
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>

          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <InfoIcon 
                onClick={handleRightDrawerOpen}
              />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        openSecondary={true}
        classes={{
          paper: clsx(classes.drawerPaper, openLeft && classes.drawerPaperClose),
        }}
        openLeft={openLeft}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleLeftDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{secondaryListItems}</List>
      </Drawer>
      <div>
        <Button type='visible' onClick={handleRightDrawerClose}/>
        <Drawer 
          anchor='right'
          variant="permanent"
          openSecondary={true}
          classes={{
            paper: clsx(classes.drawerPaper, openRight && classes.drawerPaperClose),
          }}
          openRight={openRight}
        >
          <div className={classes.toolbarIcon}>
            <IconButton onClick={handleRightDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Divider />
          <List>{secondaryListItems}</List>
        </Drawer>
      </div> */}
      {/* <main className={classes.content}> */}
        <div className={clsx(containerHeightPaper, classes.content)} width='auto'/>
        {/* <Container className={classes.container}> */}
          <Grid
            container 
            justify="flex-start"
          >
            {/* Chart */}
            {/* <Grid item xs={12} md={8} lg={9}>
              <Paper className={fixedHeightPaper}>
                <Chart />
              </Paper>
            </Grid> */}
            {/* AdminServices */}
            <Grid item xs={25} md={10} lg={4}>
              <Paper className={fixedHeightPaper}>
                <AdminServices />
              </Paper>
            </Grid>
            {/* Recent Deposits */}
            <Grid item xs={12} md={10} lg={4}>
              <Paper className={fixedHeightPaper}>
                <UserServices />
              </Paper>
            </Grid>
            {/* Recent Orders */}
            <Grid item xs={12} md={10} lg={4}>
              <Paper className={fixedHeightPaper1}>
                <EngAndDev />
              </Paper>
              <Paper className={fixedHeightPaper1}>
                <Miscellenous />
              </Paper>
            </Grid>
            {/* Recent Orders */}
            {/* <Grid item xs={12} md={4} lg={4}>
              <Paper className={fixedHeightPaper}>
                <Miscellenous />
              </Paper>
            </Grid> */}
          </Grid>
        {/* </Container> */}
      {/* </main> */}
      <AppBar position="absolute" className={clsx(classes.appBar, !openLeft && classes.appBarShiftLeft, !openRight && classes.appBarShiftRight, !openRight && !openLeft && classes.appBarShiftBoth)} color="secondary">
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="open drawer"
            onClick={handleLeftDrawerOpen}
            className={clsx(classes.menuButton, !openLeft && classes.menuButtonHidden)}
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>

          </Typography>
          <IconButton color="inherit">
            <Badge badgeContent={4} color="secondary">
              <InfoIcon 
                onClick={handleRightDrawerOpen}
              />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>
      <Drawer
        variant="permanent"
        openSecondary={true}
        classes={{
          paper: clsx(classes.drawerPaper, openLeft && classes.drawerPaperClose),
        }}
        openLeft={openLeft}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleLeftDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{secondaryListItems}</List>
      </Drawer>
      <div>
        <Button type='visible' onClick={handleRightDrawerClose}/>
        <Drawer 
          anchor='right'
          variant="permanent"
          openSecondary={true}
          classes={{
            paper: clsx(classes.drawerPaper, openRight && classes.drawerPaperClose),
          }}
          openRight={openRight}
        >
          <div className={classes.toolbarIcon}>
            <IconButton onClick={handleRightDrawerClose}>
              <ChevronLeftIcon />
            </IconButton>
          </div>
          <Divider />
          <List>{secondaryListItems}</List>
        </Drawer>
      </div>
    </div>
  );
}
