import React, { Component } from "react"
import { render } from "react-dom"
import { Router, Link } from "@reach/router"
import Main from './Main';

const Home = () => {
    return(
        <div>
        <h1>Home</h1>
        <nav>
            <Link to="/">Home</Link> |{" "}
            <Link to="dashboard">Dashboard</Link>
        </nav>
        </div>
    )
}
const Dash = () => {
    return(
        <div display="inline-block">
        <h1>Dash</h1>
        <nav>
            <Link to="/">Home</Link> |{" "}
            <Link to="/app_overview">App Overview</Link>
        </nav>
        </div>
    )
}


class Route extends Component {

    // constructor(props) {
    //     super(props);
    //     this.state={};
    // }

    render() {
        return (
            <Router>
                <Main path="/" />
                <Dash path="dashboard" />
            </Router>
        );
    }
}

export default Route;
